import Button from 'components/Button'
import Form from 'components/Form'
import TextField from 'components/TextField'
import { useCategoryContext } from 'contexts/Categories'
import { useState } from 'react'
import styles from './Categories.module.css'

const Categories = () => {

    const [descripton, setDescription] = useState('')
    const { categories, addCategory } = useCategoryContext()

    const saveCategory = (events) => {
        events.preventDefault()
        addCategory({ descripton })
    }

    const submit = e => {
        e.preventDefault()
        fetch('https://62edd141a785760e676f8b55.mockapi.io/api/v1/categories', {
            method: 'POST',
            body: JSON.stringify({ descripton }),
            headers: { 'Content-Type': 'application/json' },
        })
            .then(response => {
                console.log(response)
                response.json()
            })
            .then(json => setDescription(json.descripton))
    }

    return (
        <div>
            <Form submit={submit} title='Preencha os campos para criar a categoria'>
                <TextField
                    changeValue={value => setDescription(value)}
                    title='Descrição'
                    value={descripton} />
                <Button
                    title='Criar Categoria'
                    action={saveCategory}
                />
            </Form>
            {/* <div className={styles.categoryList}>
                {categories.map((category) => {
                    return <label>{category.descripton}</label>
                })}
            </div> */}
        </div>
    )
}

export default Categories
