import Button from 'components/Button'
import Form from 'components/Form'
import TextField from 'components/TextField'
import { useCategoryContext } from 'contexts/Categories'
import { useEffect, useState } from 'react'
import styles from './Categories.module.css'

const Categories = () => {

    const [descripton, setDescription] = useState('')
    const { categories, addCategory } = useCategoryContext()

    let headers = new Headers();

    headers.append('Authorization', 'Bearer ' + 'eyJhbGciOiJIUzUxMiJ9.eyJzdWIiOiJtYXVyaWNpb2FwYXJlY2lkb2dhYnJpZWxAZ21haWwuY29tIiwiZXhwIjoxNjczNDQwODA2fQ.94bEiOL04VtBLBvKWNzKGMXLZ2rRA3Ns_ZOQ4qZtq329u-AEiIdUec6SqjgsT7Waz_SiCG80-KFsrcw1dIPZfw');
    headers.append('Content-Type', 'application/json');

    useEffect(() => {
        fetch('http://localhost:8080/login', {
            method: 'POST',
            // headers: headers
        })
            .then(response => response.json())
            .then(data => {
                console.log(data)
            })
    }, [])

    const createCategory = () => {
        return fetch("http://localhost:8080/categories", {
            method: "POST",
            headers: headers,
            body: JSON.stringify({ 
                desc: descripton
             })
        }).then((response) => {
            return response.json();
        }).then(data => {
            console.log(data)
        })
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        createCategory()
    }

    return (
        <div>
            <Form submit={handleSubmit} title='Preencha os campos para criar a categoria'>
                <TextField
                    changeValue={value => setDescription(value)}
                    title='Descrição'
                    value={descripton} />
                <Button
                    title='Criar Categoria'
                />
            </Form>
            {/* <div className={styles.categoryList}>
                {categories.map((category) => {
                    return <label>{category.descripton}</label>
                })}
            </div> */}
        </div>
    )
}

export default Categories
