import Button from 'components/Button'
import Form from 'components/Form'
import CustomModal from 'components/CustomModal'
import { Modal } from 'react-modal'
import TextField from 'components/TextField'
import { useCategoryContext } from 'contexts/Categories'
import { useEffect, useState } from 'react'
import styles from './Categories.module.css'

const Categories = () => {

    const [descripton, setDescription] = useState('')
    const { categories, addCategory } = useCategoryContext()
    const [token, setToken] = useState('')
    const [isOpenModal, setIsOpenModal] = useState(false);

    const login = {
        username: "mauricioaparecidogabriel@gmail.com",
        password: "12345678"
    }

    useEffect(() => {
        fetch('http://localhost:8080/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                Accept: 'application/json',
            },
            body: JSON.stringify(login)
        })
            .then(response => response.json())
            .then(data => {
                console.log(data)
                var formattedToken = JSON.stringify(data).substring(10, JSON.stringify(data).length - 2)
                setToken(`Bearer ${formattedToken}`)
            })
    }, [])

    const createCategory = () => {
        return fetch("http://localhost:8080/categories", {
            method: "POST",
            headers: {
                'Authorization': token,
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                desc: descripton
            })
        }).then((response) => {
            if(response.status == 201){
                alert('Categoria criada com sucesso!')
            }
            return response.json();
        }).then(data => {
            console.log(data)
        })
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        createCategory()
    }

    const handleOpenModal = () => {
        setIsOpenModal(true)
    }

    const handleCloseModal = () => {
        setIsOpenModal(false)
    }

    return (
        <div>
            <Form submit={handleSubmit} title='Preencha os campos para criar a categoria'>
                <TextField
                    changeValue={value => setDescription(value)}
                    title='Descrição'
                    value={descripton} />
                <Button
                    // action={() => setIsOpenModal(true)}
                    title='Criar Categoria'
                />
            </Form>
            {/* <CustomModal open={isOpenModal} /> */}
            {/* <div className={styles.categoryList}>
                {categories.map((category) => {
                    return <label>{category.descripton}</label>
                })}
            </div> */}
        </div>
    )
}

export default Categories
