import Button from 'components/Button'
import Form from 'components/Form'
import CustomModal from 'components/CustomModal'
import TextField from 'components/TextField'
import { useCategoryContext } from 'contexts/Categories'
import { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { Route, Routes } from "react-router-dom"
import styles from './Categories.module.css'
import Header from 'components/Header'
import Cookies from 'universal-cookie'
import QuestionTabList from 'components/QuestionTabList'
import QuestionCard from 'components/QuestionCard'

const Categories = () => {

    const [descripton, setDescription] = useState('')
    const { categories, addCategory } = useCategoryContext()
    const [isOpenModal, setIsOpenModal] = useState(false)
    const [messageModal, setMessageModal] = useState('')
    const [success, setSuccess] = useState(false)

    const [visibility, setVisibility] = useState('list-item')
    const [showQuestionList, setShowQuestionList] = useState(false)

    const navigate = useNavigate()

    const cookies = new Cookies()

    const createCategory = () => {
        return fetch("http://localhost:8080/categories", {
            method: "POST",
            headers: {
                'Authorization': cookies.get('token'),
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                desc: descripton
            })
        }).then((response) => {
            if (response.status == 201) {
                setMessageModal('Categoria criada com sucesso!')
                setIsOpenModal(true)
                setSuccess(true)
                setDescription('')
            }
            else if(response.status == 403){
                navigate('/');
            }
            else {
                setMessageModal('Não foi possível criar categoria!')
                setIsOpenModal(true)
                setSuccess(false)
            }

            return response.json();
        }).then(data => {
            console.log(data)
        })
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        createCategory()
    }

    const handleCloseModal = () => {
        setIsOpenModal(false)
    }

    const handleShowQuestionList = () => {
        setShowQuestionList(!showQuestionList)
        if(showQuestionList == true){
            setVisibility('list-item')
        }
        else{
            setVisibility('none')
        }
    }

    return (
        <>
            <Routes>
                <Route path='*' element={<Header />} />
            </Routes>
            <div>
                <Form submit={handleSubmit} title='Preencha os campos para criar a categoria'>
                    <TextField
                        changeValue={value => setDescription(value)}
                        type='text'
                        title='Descrição'
                        value={descripton} />
                    <Button
                        title='Criar Categoria'
                    />
                </Form>
                <CustomModal
                    open={isOpenModal}
                    close={handleCloseModal}
                    success={success}>
                    <h3>{messageModal}</h3>
                </CustomModal>
                <QuestionTabList title='Exibir categorias' show={handleShowQuestionList} />

                <div className={styles.categories}>
                    {questions.map(category =>
                        <QuestionCard
                            title={category.desc}
                            visibility={visibility}
                        />)}
                </div>
            </div>
        </>
    )
}

export default Categories
