import Button from 'components/Button'
import Form from 'components/Form'
import TextField from 'components/TextField'
import { useCategoryContext } from 'contexts/Categories'
import { useState } from 'react'
import styles from './Categories.module.css'

const Categories = () => {

    const [descripton, setDescription] = useState('')
    const { categories, addCategory } = useCategoryContext()

    const saveCategory = (events) => {
        events.preventDefault()
        addCategory({ descripton })
    }

    const setItem = () => {
        return fetch("https://62edd141a785760e676f8b55.mockapi.io/api/v1/categories", {
            method: "POST",
            body: JSON.stringify({
                desc: 'teste'
            })
        }).then((response) => {
            console.log(response);
            return response.json();
        });
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        setItem()
    }

    return (
        <div>
            <Form submit={handleSubmit} title='Preencha os campos para criar a categoria'>
                <TextField
                    changeValue={value => setDescription(value)}
                    title='Descrição'
                    value={descripton} />
                <Button
                    title='Criar Categoria'
                    action={saveCategory}
                />
            </Form>
            <div className={styles.categoryList}>
                {categories.map((category) => {
                    return <label>{category.descripton}</label>
                })}
            </div>
        </div>
    )
}

export default Categories
