import Button from 'components/Button'
import Form from 'components/Form'
import TextField from 'components/TextField'
import { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import styles from './Login.module.css'

const Login = () => {

    const [descripton, setDescription] = useState('')
    onst [token, setToken] = useState('')
    const navigate = useNavigate();

    const login = () => {
        fetch('http://localhost:8080/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                Accept: 'application/json',
            },
            body: JSON.stringify(login)
        })
            .then(response => response.json())
            .then(data => {
                console.log(data)
                var formattedToken = JSON.stringify(data).substring(10, JSON.stringify(data).length - 2)
                setToken(`Bearer ${formattedToken}`)
            })
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        navigate('/home');
    }

    return (
        <>
            <Form submit={handleSubmit}>
                <TextField
                    changeValue={value => setDescription(value)}
                    title='Nome de usuÃ¡rio'
                    value={descripton} />
                <TextField
                    changeValue={value => setDescription(value)}
                    title='Senha'
                    value={descripton} />
                <Button
                    title='Login'
                // action={navigate('/questions')}
                />
            </Form>
        </>
    )
}

export default Login
