import styles from './Questions.module.css'
import { useEffect, useState } from 'react'
import QuestionCard from 'components/QuestionCard'
import TextField from 'components/TextField'
import DropDownList from 'components/DropDownList'
import TextFieldAdd from 'components/TextFieldAdd'
import Button from 'components/Button'
import Form from 'components/Form'

const Questions = () => {

    const [title, setTitle] = useState('')
    const [category, setCategory] = useState('')
    const [level, setLevel] = useState('')
    const [alternative, setAlternative] = useState('')
    const [alternatives, setAlternatives] = useState([])    
    const [answer, setAnswer] = useState([])    

    const [questions, setQuestions] = useState([])
    const [categories, setCategories] = useState([])
    const [levels, setLevels] = useState([])

    let headers = new Headers();

    headers.append('Authorization', 'Bearer ' + 'eyJhbGciOiJIUzUxMiJ9.eyJzdWIiOiJtYXVyaWNpb2FwYXJlY2lkb2dhYnJpZWxAZ21haWwuY29tIiwiZXhwIjoxNjcyNjA5Mjg1fQ.fet2uVlkTDHTXccFRiL16NXqXadg62pi5KoHBydiBkRxkNbsqq9Q-SGZqF2b8P5-0ye2siI2f8_p1ucfY-DDiw');

    useEffect(() => {
        fetch('https://62edd141a785760e676f8b55.mockapi.io/api/v1/questions', {
            method: 'GET',
            headers: headers
        })
            .then(response => response.json())
            .then(data => {
                setQuestions(data)
            })
    }, [])

    useEffect(() => {
        fetch('https://62edd141a785760e676f8b55.mockapi.io/api/v1/categories', {
            method: 'GET',
            headers: headers
        })
            .then(response => response.json())
            .then(data => {
                setCategories(data)
            })
    }, [])

    useEffect(() => {
        fetch('https://62edd141a785760e676f8b55.mockapi.io/api/v1/levels', {
            method: 'GET',
            headers: headers
        })
            .then(response => response.json())
            .then(data => {
                setLevels(data)
            })
    }, [])

    const createQuestion = () => {
        return fetch("https://62edd141a785760e676f8b55.mockapi.io/api/v1/questions", {
            method: "POST",
            headers: {'Content-Type':'application/json'},
            body: JSON.stringify({ 
                title: title,
                idCategory: category,
                idLevel: level,
                alternatives: alternatives,
                answer: answer,
             })
        }).then((response) => {
            return response.json();
        }).then(data => {
            console.log(data)
        })
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        createQuestion()
    }

    return (
        <div>
            <Form submit={handleSubmit} title='Preencha os campos para criar a pergunta'>
                <TextField
                    changeValue={value => setTitle(value)}
                    title='Título'
                    value={title} />
                <DropDownList
                    value={category}
                    changeValue={value => setCategory(value)}
                    category='Categoria'
                    items={categories.map(category => category.desc)} />
                <DropDownList
                    value={level}
                    changeValue={value => setLevel(value)}
                    category='Nível'
                    items={levels.map(level => level.desc)} />
                <label>Alternativa</label>
                <TextFieldAdd
                    addAlternative={() => { }}
                    changeValue={value => setAlternative(value)}
                    value={alternative}>
                    <img src='/images/add.png' />
                </TextFieldAdd>
                <TextField
                    changeValue={value => setAnswer(value)}
                    title='Resposta'
                    value={answer} />
                <Button title='Criar Pergunta' />
            </Form>
            <div className={styles.questionList}>
                {questions.map(
                    question => <div className={styles.cards}>
                        <QuestionCard
                            key={question}
                            title={question.title}
                            alternatives={question.alternatives}
                        />
                    </div>
                )}
            </div>
        </div>
    )
}

export default Questions
