    //                 // var formattedToken = JSON.stringify(response.body).substring(10, JSON.stringify(response.body).length - 2)
    //                 // setToken(`Bearer ${formattedToken}`)
    //                 console.log('RESPONSE: ' + JSON.stringify(response.body))
