            //     // cookies.set('token', formattedToken)
            // })
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        login()
    }

    const handleCloseModal = () => {
        setIsOpenModal(false)
    }

    return (
        <>
            <Form submit={handleSubmit}>
                <TextField
                    changeValue={value => setUsername(value)}
